import 'dart:convert';
import 'dart:io';

import 'package:defensacivilapp/screens/Post-Login/Situaciones.dart';
import 'package:defensacivilapp/screens/Post-Login/token.dart';
import 'package:flutter/material.dart';
import 'package:http/http.dart' as http;
import 'package:image_picker/image_picker.dart';
import 'package:provider/provider.dart';

class Report extends StatelessWidget {
  const Report({super.key});

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        iconTheme: IconThemeData(
          color: Colors.white,
        ),
        backgroundColor: Color.fromARGB(255, 0, 0, 0),
        title: Text(
          'Reportar Situacion',
          style: TextStyle(color: Colors.white),
        ),
      ),
      body: SingleChildScrollView(
        child: Container(
          padding: EdgeInsets.symmetric(vertical: 20, horizontal: 30),
          // decoration: BoxDecoration(
          //   image: DecorationImage(image: AssetImage(""), fit: BoxFit.cover),
          // ),
          child: ReportForm(),
        ),
      ),
    );
  }
}

class ReportForm extends StatefulWidget {
  @override
  ReportFormState createState() => ReportFormState();
}

class ReportFormState extends State<ReportForm> {
  final _formKey = GlobalKey<FormState>();
  final _tituloController = TextEditingController();
  final _descripcionController = TextEditingController();
  late String _fotoController;
  final _latitudController = TextEditingController();
  final _longitudController = TextEditingController();
  bool exito = false;
  bool noExito = false;

  final ImagePicker _picker = ImagePicker();
  late XFile? _imageFile = null;
  // final _fotoController = TextEditingController();
  Future<void> _getImage() async {
    final pickedFile = await _picker.pickImage(source: ImageSource.gallery);
    if (pickedFile != null) {
      setState(() {
        _imageFile = pickedFile;
        pickedFile.readAsBytes().then((value) {
          _fotoController = base64Encode(value);
        });
      });
    }
  }

  @override
  void initState() {
    super.initState();
  }

  void reportar(String token) async {
    var url =
        Uri.parse('https://adamix.net/defensa_civil/def/nueva_situacion.php');
    var response = await http.post(url, body: {
      'token': token,
      'titulo': _tituloController.text,
      'descripcion': _descripcionController.text,
      'foto':
          "",
      'latitud': _latitudController.text,
      'longitud': _longitudController.text,
    });

    if (response.statusCode == 200) {
      print(token);
      print(_fotoController);
      print('Registro exitoso');
      print('Respuesta: ${response.body}');

      // Verificar si el registro fue exitoso
      var jsonResponse = json.decode(response.body);
      if (jsonResponse['exito'] == true) {
        _tituloController.text = "";
        _descripcionController.text = "";
        _fotoController = "";
        _latitudController.text = "";
        _longitudController.text = "";

        print('EL registro se realizo');
        setState(() {
          exito = true;
          noExito = false;
        });
        await Future.delayed(Duration(seconds: 2));

        Navigator.push(
          context,
          MaterialPageRoute(builder: (context) => Situaciones()),
        );
        // await Future.delayed(Duration(seconds: 2));
        // Navigator.pop(context);
      } else {
        setState(() {
          noExito = true;
          exito = false;
        });
      }
      // Aquí puedes procesar la respuesta, como guardar el token de sesión, etc.
    } else {
      print('Error al registrar');
      print('Código de estado: ${response.statusCode}');
      print('Mensaje de error: ${response.body}');
    }
  }

  @override
  Widget build(BuildContext context) {
    return Form(
      key: _formKey,
      child: Column(
        mainAxisAlignment: MainAxisAlignment.start,
        children: [
          campoTitulo(),
          SizedBox(
            height: 10,
          ),
          campoDescripcion(),
          SizedBox(
            height: 10,
          ),
          campoFoto(),
          SizedBox(
            height: 10,
          ),
          campoLatitud(),
          SizedBox(
            height: 10,
          ),
          campoLongitud(),
          if (exito)
            Text('Registro creado con exito',
                style: TextStyle(color: Colors.green)),
          if (noExito)
            Text('No fue posible el registro',
                style: TextStyle(color: Colors.red)),
          SizedBox(
            height: 30,
          ),
          boton(),
        ],
      ),
    );
  }

  Widget campoTitulo() {
    return TextFormField(
      controller: _tituloController,
      decoration: InputDecoration(
        border: OutlineInputBorder(),
        hintText: "Título del evento o situación",
        fillColor: Colors.white,
        filled: true,
      ),
      validator: (value) {
        if (value == null || value.isEmpty) {
          return 'Por favor, introduce el título';
        }
        return null;
      },
    );
  }

  Widget campoDescripcion() {
    return TextFormField(
      controller: _descripcionController,
      decoration: InputDecoration(
        border: OutlineInputBorder(),
        hintText: "Descripción completa de lo ocurrido",
        fillColor: Colors.white,
        filled: true,
      ),
      validator: (value) {
        if (value == null || value.isEmpty) {
          return 'Por favor, introduce la descripción';
        }
        return null;
      },
    );
  }

  Widget campoFoto() {
    return InkWell(
      onTap: _getImage,
      child: Container(
        height: 200,
        decoration: BoxDecoration(
          color: Colors.grey[200],
          border: Border.all(color: Colors.grey),
        ),
        child: _imageFile != null
            ? Image.file(
                File(_imageFile!.path),
                fit: BoxFit.cover,
              )
            : Center(
                child: Icon(
                  Icons.camera_alt,
                  size: 50,
                ),
              ),
      ),
    );
  }

  Widget campoLatitud() {
    return TextFormField(
      controller: _latitudController,
      decoration: InputDecoration(
        border: OutlineInputBorder(),
        hintText: "Latitud",
        fillColor: Colors.white,
        filled: true,
      ),
      validator: (value) {
        if (value == null || value.isEmpty) {
          return 'Por favor, introduce la latitud';
        }
        return null;
      },
    );
  }

  Widget campoLongitud() {
    return TextFormField(
      controller: _longitudController,
      decoration: InputDecoration(
        border: OutlineInputBorder(),
        hintText: "Longitud",
        fillColor: Colors.white,
        filled: true,
      ),
      validator: (value) {
        if (value == null || value.isEmpty) {
          return 'Por favor, introduce la longitud';
        }
        return null;
      },
    );
  }

  Widget boton() {
    final tokenProvider = Provider.of<AuthToken>(context, listen: false);

    return FractionallySizedBox(
      widthFactor: 0.9, // Factor de ancho para ajustar el tamaño del botón
      child: TextButton(
        onPressed: () {
          if (_formKey.currentState != null &&
              _formKey.currentState!.validate()) {
            reportar(tokenProvider.token);
          }
        },
        style: TextButton.styleFrom(
          padding: EdgeInsets.symmetric(horizontal: 100, vertical: 10),
          backgroundColor: Color.fromARGB(255, 0, 0, 0),
          shape: const RoundedRectangleBorder(
            borderRadius: BorderRadius.all(Radius.circular(30)),
          ),
          foregroundColor: Colors.white,
        ),
        child: Text(
          "Reportar",
          style: TextStyle(fontSize: 20),
        ),
      ),
    );
  }
}
